import {
    SimpleGrid,
    Skeleton,
    Container,
    Stack,
    useMantineTheme,
    px,
  } from '@mantine/core';

  const getChild = (height) => <Skeleton height={height} radius="md" animate={false} />;
  const BASE_HEIGHT = 360;

  const getSubHeight = (children, spacing) =>
    BASE_HEIGHT / children - (spacing * (children - 1)) / children;

  export function Subgrid() {
    const theme = useMantineTheme();
    const gridStyle = {
      backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[7] : theme.black,
      borderRadius: theme.radius.md,
      padding: px(theme.spacing.md),
    };

    return (
      <Container my="md">
        <SimpleGrid cols={{ base: 1, xs: 4 }} style={gridStyle}>
          {getChild(BASE_HEIGHT)}
          <Stack>
            {getChild(getSubHeight(2, px(theme.spacing.md)))}
            {getChild(getSubHeight(2, px(theme.spacing.md)))}
          </Stack>
          <Stack>
            {getChild(getSubHeight(3, px(theme.spacing.md)))}
            {getChild(getSubHeight(3, px(theme.spacing.md)))}
            {getChild(getSubHeight(3, px(theme.spacing.md)))}
          </Stack>
          {getChild(BASE_HEIGHT)}
        </SimpleGrid>
      </Container>
    );
  }
