import { useState } from 'react';
import { Text, Button, Box } from '@chakra-ui/react';

function FileForm({ task, endpoint}) {
    const [file, setFile] = useState(null);

    const [results, setResults] = useState(null); // New state variable for results

    const handleFileInputChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('file_upload', file);

        try {
            const response = await fetch(endpoint, {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const data = await response.json();
                onUpload(task, data[`${task} Results`]); // Call the onUpload callback

                // if (task == 'Frontal')
                //     console.log(data['Frontal Results']);

                // else if (task == 'Frontal-Smile')
                //     console.log(data['Frontal-Smile Results']);

                // else if (task == 'Sides')
                //     console.log(data['Sides Results']);
            } else {
                console.error('Failed to upload file');
            }
        } catch (error) {
            console.log(error);
        }
    };

    return (
        <Box alignItems={'center'}>
            <Text textAlign={'center'} fontSize={'x-large'}>
                {task}
            </Text>

            <Box alignItems={'center'}>
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: '20px' }}>
                        <input type='file' onChange={handleFileInputChange} />
                    </div>

                    <Button colorScheme='blue' size='md' type='submit'>
                        Upload
                    </Button>
                </form>
            </Box>
        </Box>
    );
}

export default FileForm;
