import { useState } from 'react';
import { Title, Button } from '@chakra-ui/react'

const customForm = {
    task: String
}

function FileForm(customForm) {

    const [file, setFile] = useState(null); 

    const handleFileInputChange = (event) => {
        setFile(event.target.files[0])
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('file_upload', file);

        try {
            const endpoint = 'http://127.0.0.1:8000/predict/'
            const response = await fetch(endpoint, {
                method: 'POST'
                , body: formData
            });

            if (response.ok) {
                const data = await response.json()
                console.log(data['Results'])
            } else {
                console.error('Failed to upload file');
            }
        } catch(error) {
            console.log(error)
        }
    }

    return (
        <div>
            <Title>{customForm.task}</Title>
            {/* <h1>{customForm.task}</h1> */}

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: "20px" }}>
                    <input type='file' onChange={handleFileInputChange} />
                </div>

                <Button colorScheme='blue' size='md' type='submit'>Upload</Button>
            </form>
        </div>
    )
}

export default FileForm
